var Configstore=require("configstore"),config=new Configstore("rdownload"),inquirer=require("inquirer"),questions=[{type:"input",name:"host",message:"Enter the remote host(IP):",validate:function(e){return!!e.length||"Please enter a host(IP)"}},{type:"input",name:"username",message:"Enter the username of remote server:",default:"root",validate:function(e){return!!e.length||"Please enter the username."}}];module.exports={get:function(e){return config.get(e)},set:function(e,t){return config.set(e,t)},askQuestions:function(){return inquirer.prompt(questions)}};