var spawn=require("child_process").spawn,get=require("./config").get,userName=get("username"),host=get("host");module.exports={ls:function(){spawn("ssh",[userName+"@"+host,"ls -lh ~"]).stdout.on("data",function(o){console.log("stdout: "+o)})},download:function(o){spawn("rsync",["-avz","--progress","--partial-dir=.rsync-partial",userName+"@"+host+":~/"+o,"."]).stdout.on("data",function(o){console.log("stdout: "+o)})},upload:function(o,s){var t="wget -bqc ";t+="-O "+o+" ",spawn("ssh",[userName+"@"+host,t+=""+s]).stdout.on("data",function(o){console.log("stdout: "+o)})},verify:function(){var o=spawn("ssh",["-T",userName+"@"+host,"echo valid"]);o.stdout.on("data",function(o){console.log(""+o)}),o.on("exit",function(o){0<o&&console.log("error: "+o)})}};